import { Component } from '@angular/core';
import { FormBuilder, Validators } from "@angular/forms";
import { Message } from "primeng/api";

const braintree = require('braintree-web');

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent {
  userForm = this.fb.group({
    number: ['', [
      Validators.required,
      Validators.pattern(/^\d{4}(\s\d{4}){3}$/), // Pattern validator for 16-digit credit card numbers with spaces
      Validators.maxLength(19) // Maximum length of 16 digits with spaces
    ]],
    cvc: ['', [
      Validators.required,
      Validators.pattern(/^\d{3}$/) // Pattern validator for 3-digit CVV codes
    ]],
    date: ['', [
      Validators.required,
      Validators.pattern(/^\d{2}\/\d{2}$/), // Pattern validator for MM/YYYY date format
      Validators.maxLength(5) // Maximum length of 5 digits
    ]],
    postal: ['', [
      Validators.required,
      Validators.pattern(/^[\w\d]{6}$/), // Pattern validator for postal code (6 alphanumeric characters)
    ]],
  });

  braintreeClient: any; // Declare the braintreeClient property

  constructor(private fb: FormBuilder) {}

  messages: Message[] | undefined;

  ngOnInit{

  }

  addMessages() {
    this.messages = [
      { severity: 'success', summary: 'Success', detail: 'Message Content' },
      { severity: 'info', summary: 'Info', detail: 'Message Content' },
      { severity: 'warn', summary: 'Warning', detail: 'Message Content' },
      { severity: 'error', summary: 'Error', detail: 'Message Content' }
    ];
  }

  formatCardNumber(event: any) {
    const input = event.target as HTMLInputElement;
    const trimmedValue = input.value.replace(/\s+/g, ''); // Remove existing spaces
    let formattedValue = '';
    for (let i = 0; i < trimmedValue.length; i++) {
      if (i % 4 === 0 && i > 0) {
        formattedValue += ' ';
      }
      formattedValue += trimmedValue.charAt(i);
    }
    input.value = formattedValue;
  }

  formatExpiryDate(event: any) {
    const input = event.target as HTMLInputElement;
    const trimmedValue = input.value.replace(/\s+/g, ''); // Remove existing spaces
    const formattedValue = trimmedValue.replace(/[^0-9]/g, ''); // Remove non-numeric characters

    if (formattedValue.length > 4) {
      input.value = formattedValue.slice(0, 4);
    } else {
      let result = '';
      for (let i = 0; i < formattedValue.length; i++) {
        if (i === 2) {
          result += '/';
        }
        result += formattedValue.charAt(i);
      }
      input.value = result;
    }
  }

  processPayment() {
    console.log(this.userForm.value);
  }



}
<div class="grid-container fade-in-up">

  <p-card>
    <div class="svg-icon">
      <img src="../../assets/paypal_logo.svg" alt="SVG Icon" class="logo-icon" />
    </div>
    <form [formGroup]="userForm" (ngSubmit)="processPayment()">
      <div class="grid-container">
        <div class="field">
          <label class="block">Card Number</label>
          <input pInputText placeholder="1000 1000 1000 1000" formControlName="number" maxlength="19" (input)="formatCardNumber($event)" />
        </div>
        <div class="field">
          <label class="block">Postal Code</label>
          <input pInputText placeholder="L1L1L1" maxlength="6" formControlName="postal" />
        </div>
        <div class="field">
          <label class="block">CVV Code</label>
          <input type="text" pInputText placeholder="000" maxlength="3" formControlName="cvc" />
        </div>
        <div class="field">
          <label class="block">Expiry Date</label>
          <input type="text" pInputText placeholder="07/24" formControlName="date" maxlength="5" (input)="formatExpiryDate($event)" />
        </div>
      </div>
      <p-button label="Purchase" type="submit" class="submit" styleClass="p-button-outlined" [disabled]="userForm.invalid"></p-button>
      <p-messages [enableService]="false" [closable]="false"></p-messages>
    </form>
  </p-card>
</div>
