import { Component } from '@angular/core';
import {FormBuilder, Validators} from "@angular/forms";

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent {

  userForm = this.fb.group({
    name: ['', [
      Validators.required, // Required field validator
      Validators.pattern(/^[A-Za-z0-9-' ]+$/) // Regular expression pattern validator
    ]],
    number: ['', [
      Validators.pattern(/^\d{13}$/) // Regular expression pattern validator for UPC code format
    ]],
    cvc: ['', [
      Validators.pattern(/^\d{5}$/) // Regular expression pattern validator for UPC code format
    ]],
    date: ['', [
      Validators.pattern(/^\d{5}$/) // Regular expression pattern validator for UPC code format
    ]],
  });

  constructor(private fb: FormBuilder) {};
  processPayment() {
    console.log(this.userForm.value);
  }

}
<p-card>
  <div class="svg-icon">
    <img src="../../assets/paypal_logo.svg" alt="SVG Icon" />
  </div>
  <form [formGroup]="userForm" (ngSubmit)="processPayment()">
    <div class="field">
      <label class="block">Name</label>
      <input type="text" pInputText placeholder="John Doe" formControlName="name"/>
      <small class="p-error-block" *ngIf="userForm.get('name')?.invalid">Name is required.</small>
    </div>
    <div class="field">
      <label class="block">Card Number</label>
      <input type="text" pInputText placeholder="1000100010001000" formControlName="number"/>
    </div>
    <div class="field">
      <label class="block">CVC</label>
      <input type="text" pInputText placeholder="000" formControlName="cvc"/>
    </div>
    <div class="field">
      <label class="block">Expiry Date</label>
      <input type="text" pInputText placeholder="0724" formControlName="date"/>
    </div>
    <!--  <p-inputMask mask="99-999999" placeholder="99-999999"></p-inputMask>-->
    <p-button label="Submit"  [disabled]="userForm.invalid"></p-button>

  </form>
</p-card>
